package codigofacilitojava;

/**
 * @author flipas
 *
 */
class swicthcase {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		switch(x)
		{
			case 0:
			{
				System.out.println("ESTA OPCION 0");
				break;
			}
			case 2:
			{
				System.out.println("ESTA OPCION 2");
				break;
			}
			case 3:
			{
				System.out.println("ESTA OPCION 3");
				break;
			}
			default:
			{
				System.out.println("OPCION DEFAULT");
			}
		}
	}

}
